mapper.select:
select 

from l_lottery_num
where lottery_num_id = #{lotteryNumId,jdbcType=BIGINT}
  and lottery_type_id = #{lotteryTypeId,jdbcType=BIGINT}

mapper.delete:
delete from l_lottery_num
where lottery_num_id = #{lotteryNumId,jdbcType=BIGINT}
  and lottery_type_id = #{lotteryTypeId,jdbcType=BIGINT}

mapper.update:
update l_lottery_num
set lottery_num = #{lotteryNum,jdbcType=VARCHAR},
  win_num = #{winNum,jdbcType=VARCHAR},
  create_date = #{createDate,jdbcType=TIMESTAMP},
  update_date = #{updateDate,jdbcType=TIMESTAMP},
  create_user = #{createUser,jdbcType=BIGINT},
  update_user = #{updateUser,jdbcType=BIGINT}
where lottery_num_id = #{lotteryNumId,jdbcType=BIGINT}
  and lottery_type_id = #{lotteryTypeId,jdbcType=BIGINT}
